query GetKnowledgeGraph($knowledgeBaseId: ID!, $limit: Int, $maxDepth: Int, $label: String) {
  knowledgeGraph(
    knowledgeBaseId: $knowledgeBaseId
    limit: $limit
    maxDepth: $maxDepth
    label: $label
  ) {
    entities {
      id
      type
      name
      properties
      importanceScore
      __typename
    }
    relationships {
      id
      source
      target
      type
      properties
      weight
      __typename
    }
    stats {
      totalEntities
      totalRelationships
      totalDocuments
      totalChunks
      entityTypes
      relationshipTypes
      __typename
    }
    __typename
  }
}

