query GetConversation($id: ID!) {
  conversation(id: $id) {
    id
    title
    summary
    metadata
    messages {
      id
      parts {
        ... on TextPart {
          id
          text
          __typename
        }
        ... on ToolCallPart {
          id
          toolCall {
            id
            name
            arguments
            result
            error
            status
            __typename
          }
          __typename
        }
        __typename
      }
      profile {
        id
        type
        name
        avatarUrl
        __typename
      }
      timestamp
      conversationId
      parentId
      metadata
      __typename
    }
    __typename
  }
}

